public class StringsSecondAssigments {

    public int findStopCodon (String dna, int startIndex, String stopCodon){
        int stopCodonIndex = dna.indexOf(stopCodon, startIndex);
        if ((stopCodonIndex - startIndex) % 3 == 0) 
            return stopCodonIndex;
        return dna.length(); 
    }
    
    public String findGene (String dna){
        int startIndex = dna.indexOf("ATG");
        if (startIndex == -1)
            return "";
        int stopIndex = findStopCodon(dna, startIndex + 3, "TAA");
        if (stopIndex == dna.length())
            return "";
        return dna.substring(startIndex, stopIndex);
    }
    
    public int howMany(String stringa, String stringb) {
        int times = 0;
        int index = 0;
        int startIndex = 0;
        
        while (index != -1){
            index = stringb.indexOf(stringa, startIndex);
            times += 1;
            startIndex = index + stringa.length();
        }
        return times;
    }
    
    public int countGenes (String dna){
        int countGenes = 0;
        int startIndex = 0;
        while (true){
            int geneIndex = dna.indexOf("ATG", startIndex);
            if (geneIndex == -1)
                break;
            int stopIndexTAA = findStopCodon(dna, startIndex + 3, "TAA");
            int stopIndexTAG = findStopCodon(dna, startIndex + 3, "TAG");
            int stopIndexTGA = findStopCodon(dna, startIndex + 3, "TGA");
            if (stopIndexTAA <= stopIndexTAG && stopIndexTAA <= stopIndexTAG){
                startIndex = stopIndexTAA + 3;
            } else if (stopIndexTAG <= stopIndexTAA && stopIndexTAG <= stopIndexTGA){
                startIndex = stopIndexTAG + 3;
            } else {
                startIndex = stopIndexTGA + 3;
            }
            System.out.println(dna.substring(geneIndex, stopIndexTGA).length());
            countGenes += 1;
        }
        return countGenes;
    }
    
    public void testFindStopCodon  () {
        String dna1 = "ATGTGGTAG";
        int startIndex1 = 0;
        String stopCodon1 = "TAG";
        System.out.println(findStopCodon(dna1, startIndex1, stopCodon1));
        
        String dna2 = "ATGTGGTAG";
        int startIndex2 = 0;
        String stopCodon2 = "TGA";
        System.out.println(findStopCodon(dna2, startIndex2, stopCodon2));
    }
    
    public void testFindGene(){
        String dna1 = "ATGTGGTAG";
        System.out.println(dna1 + " -> " + findGene(dna1));
        
        String dna2 = "ATGTGGTAA";
        System.out.println(dna2 + " -> " + findGene(dna2));
    }
    
    public void testHowMany(){
        String stringa1 = "AA";
        String stringb1 = "ATAAAA";
        System.out.println(stringb1 + " got " + howMany(stringa1, stringb1) + " times " + stringa1);
    }
    
    public void testCountGenes(){
        String dna = "";
        System.out.println(countGenes(dna));
    }
}
